import React from "react";
import { useState } from "react";

//I just installed it to the use of dynamically create a input boxes for adding incredients at the form
// import {v4 as uuid} from 'uuid'

const CreateRecipe = () => {
  const [name, setName] = useState("");
  const [ingredientsValues, setIngredientsValues] = useState([]);
  const handleAddIngredient = (event) =>{
    event.preventDefault() ; 
    setIngredientsValues([...ingredientsValues , ""]) ; 
  }

  const handleDeleteIngredient = (event,index) =>{
    event.preventDefault() ;
    const newArr = ingredientsValues
    setIngredientsValues(ingredientsValues.splice())

  }

  return (
    <form className="flex flex-col justify-center align-middle">
      <label htmlFor="name">Enter Ingredient Name : </label>
      <input type="text" name="name" id="name" />
      {ingredientsValues.map((val, index) => {
        return (
          <>
          <label htmlFor="ingredient">Enter Ingredient Names : </label>
            <input type="text" key={index} name="ingredient" id="ingredient" />
            <button key={index} onClick={(event)=>{
              handleDeleteIngredient(event,this.target.key) ;
            }}>Delete</button>
          </>
        );
      })}
      <button onClick={(event)=>{handleAddIngredient(event)}}>Add Ingredient</button>
    </form>
  );
};

export default CreateRecipe;
