import React from "react";
import { useState } from "react";

//I just installed it to the use of dynamically create a input boxes for adding incredients at the form
import {v4 as uuid} from 'uuid'

const CreateRecipe = () => {
  const [name, setName] = useState("");
  const [ingredientsValues, setIngredientsValues] = useState([]);





  const handleAddIngredientButton = (event) =>{
    event.preventDefault() ; 
    setIngredientsValues([...ingredientsValues , ""]) ; 
  }

  const handleDeleteIngredientButton = (event,index) =>{
    event.preventDefault() ;
    console.log("Iron man",index)
    const ingredientsAfterRemoval = ingredientsValues.filter((_,curIndex)=>curIndex!==index)
    setIngredientsValues(ingredientsAfterRemoval) ; 
  }

  const hangleIngredientUpdate = (index , newVal) =>{
    const ingredientsAfterUpdating = ingredientsValues.map((preVal,curIndex) => {
      if(curIndex === index) return newVal
      return preVal
    })
    setIngredientsValues(ingredientsAfterUpdating) ; 
    console.log(ingredientsValues) ;
  } 

  return (
    <form className="flex flex-col justify-center align-middle">
      <label htmlFor="name">Enter  Recipe Name : </label>
      <input type="text" name="name" id="name" />
      {ingredientsValues.map((val, index) => {
        return (
          <>
           <label htmlFor="ingredient">Enter Ingredient Names : </label>
            <input type="text"  name="ingredient" id="ingredient" onChange={(event)=>{
              hangleIngredientUpdate(index , event.target.value) ; 
            }}/>
            <button key={index} onClick={(event)=>{
              console.log("I am ")
              handleDeleteIngredientButton(event,index) ;
            }}>Delete</button>
          </>
        );
      })}

      <textarea name="recipe" id="" cols="30" rows="10"></textarea>
      <button onClick={(event)=>{handleAddIngredientButton(event)}}>Add Ingredient</button>
    </form>
  );
};

export default CreateRecipe;
