import React from 'react'
import { useState } from 'react'
import axios from 'axios'
//I just installed it to the use of dynamically create a input boxes for adding incredients at the form
// import { v4 as uuid } from "uuid";
import config from '../configuration/config'


const CreateRecipe = () => {
  const [name, setName] = useState('')
  const [ingredientsValues, setIngredientsValues] = useState([''])
  const [recipeSteps, setRecipeSteps] = useState('')
  const [imageUrl, setImageUrl] = useState('')
  const [cookingTime, setCookingTime] = useState(0)

  const handleAddIngredientButton = event => {
    event.preventDefault()
    setIngredientsValues([...ingredientsValues, ''])
  }

  const handleDeleteIngredientButton = (event, index) => {
    event.preventDefault()
    console.log('Iron man', index)
    const ingredientsAfterRemoval = ingredientsValues.filter(
      (_, curIndex) => curIndex !== index
    )
    setIngredientsValues(ingredientsAfterRemoval)
  }

  const hangleIngredientUpdate = (index, newVal) => {
    const ingredientsAfterUpdating = ingredientsValues.map(
      (preVal, curIndex) => {
        if (curIndex === index) return newVal
        return preVal
      }
    )
    setIngredientsValues(ingredientsAfterUpdating)
    console.log(ingredientsValues)
  }

  const handleCreateRecipeFormSubmit = async (event) => {
    const userId = window.localStorage.getItem("userId") ; 
    event.preventDefault() ;
    try{
      const response = await axios.post(`http://localhost:${config.backendPort}/recipe`,{
        name: name,
        ingredients : ingredientsValues.toString(),
        instructions : recipeSteps.toString(),
        imageUrl : imageUrl,
        cookingTime :  cookingTime, 
        userOwner : userId
    })
    // console.log(response.data) ; 
    alert(response.data) ; 
    }catch(err){
      if(err) console.log(err) ; 
    }
  }

  return (
    <form
      onSubmit={event => {
        handleCreateRecipeFormSubmit(event)
      }}
      className='flex flex-col justify-center align-middle'
    >
      <label htmlFor='name'>Enter Recipe Name : </label>
      <input
        type='text'
        name='name'
        id='name'
        value={name}
        onChange={event => {
          setName(event.target.value)
        }}
        required
      />
      {ingredientsValues.map((val, index) => {
        return (
          <>
            <label htmlFor='ingredient'>Enter Ingredient Names : </label>
            <input
              value={ingredientsValues[index]}
              type='text'
              name='ingredient'
              id='ingredient'
              onChange={event => {
                hangleIngredientUpdate(index, event.target.value)
              }}
              required
            />
            <button
              key={index}
              onClick={event => {
                console.log('I am ')
                handleDeleteIngredientButton(event, index)
              }}
            >
              Delete
            </button>
          </>
        )
      })}

      <button
        onClick={event => {
          handleAddIngredientButton(event)
        }}
      >
        Add Ingredient
      </button>
      <textarea
        name='recipeSteps'
        id='recipeSteps'
        cols='30'
        rows='10'
        placeholder='Enter each step in each line'
        value={recipeSteps}
        onChange={event => {
          setRecipeSteps(event.target.value)
        }}
        required
      ></textarea>
      <label htmlFor='imageUrl'>Enter the Image URL : </label>
      <input
        type='text'
        name='imageUrl'
        id='imageUrl'
        value={imageUrl}
        onChange={event => {
          setImageUrl(event.target.value)
        }}
      />
      <label htmlFor='cookingTime'>Enter Cooking Time In Minutes : </label>
      <input
        type='number'
        name='cookingTime'
        id='cookingTime'
        value={cookingTime}
        onChange={event => {
          setCookingTime(event.target.value)
        }}
      />


    </form>
  )
}

export default CreateRecipe
